(function () {

    ////////////////////////////////////////////////////////////////////////
    // NOTE: IF YOU EDIT THIS FILE
    // You must also update editor_plugin.js with a minified version.
    ////////////////////////////////////////////////////////////////////////
    tinymce.create('tinymce.plugins.EXPEDIT.MyWorkflow', {
        /**
        * Initializes the plugin, this will be executed after the plugin has been created.
        * This call is done before the editor instance has finished it's initialization so use the onInit event
        * of the editor instance to intercept that event.
        *
        * @param {tinymce.Editor} ed Editor instance that the plugin is initialized in.
        * @param {string} url Absolute URL to where the plugin is located.
        */    
        init: function (ed, url) {
            // Register the command so that it can be invoked by using tinyMCE.activeEditor.execCommand('mceMediaPicker');
            ed.addCommand('mceMyWorkflow', function () {
                ed.focus();            
                var url = "/Share/PickWorkflow";
                $.colorbox({
                    href: url,
                    iframe: true,
                    reposition: true,
                    width: "90%",
                    height: "90%",
                    onLoad: function() {
                        // hide the scrollbars from the main window
                        $('html, body').css('overflow', 'hidden');
                        //$('#cboxClose').remove();
                    },
                    onClosed: function() {
                        $('html, body').css('overflow', '');

                        var selectedData = $.colorbox.selectedData;

                        if (selectedData == null) // Dialog cancelled, do nothing
                            return;


                        
                        var selected = selectedData;
                        newId = NewGUID();
                        selected.id = newId;
                        var div = $('<div>').attr({
                            'id': newId,
                            'data-json': JSON.stringify(selected),
                            'class': 'tiny tiny-workflow mceNonEditable disable-user-select',
                            'type': 'tiny-workflow'
                        });
                        window.renderFunctions(div);
              

                        $.colorbox.selectedData = null;

           
                        // extra dev code  
                        tinyMCE.execCommand('mceReplaceContent', false, div.get(0).outerHTML + "&nbsp;");
              
                    }
                });
            });

            // Register media button
            ed.addButton('myworkflow', {
                title: jQuery("#" + ed.id).data("myworkflow-title"), //ed.getParam("mediapicker_title"),
                cmd: 'mceMyWorkflow',
                image: url + '/img/network.png'
            });

            $('#colorbox,#cboxOverlay,#cboxWrapper').css('z-index', '99999999999999999');


            // Run extra dev code
            ed.on('ExecCommand', function (e) {
                window.renderFunction['tiny-workflow-editor'](e);
            });


        },
            
        createControl: function (n, cm) {
            return null;
        },

        /**
        * Returns information about the plugin as a name/value array.
        * The current keys are longname, author, authorurl, infourl and version.
        *
        * @return {Object} Name/value array containing information about the plugin.
        */
        getInfo: function () {
            return {
                longname: 'EXPEDIT MyFormEditor Plugin',
                author: 'Andrew Grosser & Paul Knittel',
                authorurl: 'http://expedit.com.au',
                infourl: 'http://expedit.com.au',
                version: '0.1'
            };
        }
    });

    // Register plugin
    tinymce.PluginManager.add('myworkflow', tinymce.plugins.EXPEDIT.MyWorkflow);




        




})();

